import itertools


def substrings(n):
    sum_substrings = sum(map(int, (n[i:j] for i, j in itertools.combinations(range(len(n) + 1), 2))))
    return sum_substrings % (10**9 + 7)


if __name__ == '__main__':
    # assert substrings('123') == 164
    # assert substrings('16') == 23
    # assert substrings('972698438521') == 445677619
    assert substrings('') == 597988838
